import React, { Component } from 'react';
import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';

class App extends Component {


  constructor(props) {
    super(props);
    this.state = {Name:''};

    // This binding is necessary to make `this` work in the callback
    this.editData = this.editData.bind(this);
  }

  editData(event) {
    console.log(event.target.id);

  }



  render() {
    return (
      <div class="container">
      <h2 >CRUD Example</h2>            
      <table class="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>John</td>
            <td  id="edit">
              <button class="btn btn-primary a-btn-slide-text" onClick={(e) => {this.editData(e)}}>
                 <span class="glyphicon glyphicon-edit" aria-hidden="true"></span>
                 <span><strong>Edit</strong></span>            
              </button>
                 &nbsp;
                 &nbsp;
              <button class="btn btn-primary a-btn-slide-text" onClick={this.deleteData}>
                 <span class="glyphicon glyphicon-edit" aria-hidden="true"></span>
                 <span><strong>Delete</strong></span>            
              </button>
              &nbsp;
                 &nbsp;
              <button class="btn btn-primary a-btn-slide-text" onClick={this.readData}>
                 <span class="glyphicon glyphicon-edit" aria-hidden="true"></span>
                 <span><strong>Read</strong></span>            
              </button>
            </td>
          </tr>

        </tbody>
      </table>
    </div>

    );
  }
}

export default App;
