import React ,{ Component } from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';

class App extends Component {


  constructor(props) {
    super(props);
    this.state = {Name:''};

    // This binding is necessary to make `this` work in the callback
    this.editData = this.editData.bind(this);
  }

  editData(event) {
    console.log($(event.target.id).parent());


  }

  deleteData(event) {
    console.log($(event.target.id).parent());

  }

  readData(event) {
    console.log(event.target.id);

  }



  render() {
    return (
      <div class="container">
      <h2 >CRUD Example</h2>            
      <table class="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          <tr id="one">
            <td>John</td>
            <td>
              <button id="edit" class="btn btn-primary a-btn-slide-text" onClick={(e) => {this.editData(e)}}>
                 <span class="glyphicon glyphicon-edit" aria-hidden="true"></span>
                 <span><strong>Edit</strong></span>            
              </button>
                 &nbsp;
                 &nbsp;
              <button id="delete" class="btn btn-primary a-btn-slide-text" onClick={(e) => {this.deleteData(e)}}>
                 <span class="glyphicon glyphicon-edit" aria-hidden="true"></span>
                 <span><strong>Delete</strong></span>            
              </button>
              &nbsp;
                 &nbsp;
              <button id="read" class="btn btn-primary a-btn-slide-text"onClick={(e) => {this.readData(e)}}>
                 <span class="glyphicon glyphicon-edit" aria-hidden="true"></span>
                 <span><strong>Read</strong></span>            
              </button>
            </td>
          </tr>

        </tbody>
      </table>
    </div>

    );
  }
}

export default App;
